import React, { useRef, useState, useEffect } from "react";
import Webcam from "react-webcam";
import AuthWrapper from "./components/AuthWrapper";
import "./App.css";

const App = () => {
  const webcamRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [translationHistory, setTranslationHistory] = useState([]);
  const [webcamError, setWebcamError] = useState(null);
  const [confidence, setConfidence] = useState(0);
  const [user, setUser] = useState(null);

  // Check if user is already authenticated on component mount
  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    }
  }, []);

  const signLanguageData = {
    "A": "Letter A - Fist with thumb on side",
    "B": "Letter B - Flat hand with fingers together",
    "C": "Letter C - Curved hand like letter C",
    "HELLO": "Hello - Wave hand",
    "THANK YOU": "Thank you - Hand from chin forward",
    "YES": "Yes - Fist nodding up and down",
    "NO": "No - Index and middle finger tapping thumb",
    "PLEASE": "Please - Flat hand rubbing in circular motion",
    "SORRY": "Sorry - Fist making circular motion over heart",
    "HELP": "Help - One hand on top of other, both moving up"
  };

  const handleWebcamError = (error) => {
    setWebcamError("Unable to access webcam. Please check permissions.");
    console.error("Webcam error:", error);
  };

  const startRecording = () => {
    setIsRecording(true);
    setPrediction("");
    setConfidence(0);
  };

  const stopRecording = () => {
    setIsRecording(false);
  };

  const capture = async () => {
    if (!webcamRef.current) return;

    setIsLoading(true);
    const imageSrc = webcamRef.current.getScreenshot();

    try {
      await new Promise(resolve => setTimeout(resolve, 2000)); // Simulate API

      const predictions = Object.keys(signLanguageData);
      const randomPrediction = predictions[Math.floor(Math.random() * predictions.length)];
      const randomConfidence = Math.floor(Math.random() * 40) + 60;

      setPrediction(randomPrediction);
      setConfidence(randomConfidence);

      const newEntry = {
        id: Date.now(),
        prediction: randomPrediction,
        confidence: randomConfidence,
        timestamp: new Date().toLocaleTimeString(),
        description: signLanguageData[randomPrediction]
      };

      setTranslationHistory(prev => [newEntry, ...prev.slice(0, 9)]);

      if (randomConfidence > 70) {
        const utterance = new SpeechSynthesisUtterance(randomPrediction);
        utterance.rate = 0.8;
        utterance.pitch = 1;
        speechSynthesis.speak(utterance);
      }

    } catch (error) {
      console.error("Prediction error:", error);
      setPrediction("Error processing image");
    } finally {
      setIsLoading(false);
    }
  };

  const clearHistory = () => {
    setTranslationHistory([]);
  };

  const speakText = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.rate = 0.8;
    speechSynthesis.speak(utterance);
  };

  const handleAuthSuccess = (userData) => {
    setUser(userData);
    localStorage.setItem('currentUser', JSON.stringify(userData));
  };

  const handleSignOut = () => {
    setUser(null);
    localStorage.removeItem('currentUser');
    setTranslationHistory([]);
    setPrediction("");
    setConfidence(0);
  };

  // If user is not authenticated, show auth wrapper
  if (!user) {
    return <AuthWrapper onAuthSuccess={handleAuthSuccess} />;
  }

  // Main app content for authenticated users
  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <div className="header-text">
            <h1>Sign Language to Voice Converter</h1>
            <p>Real-time sign language recognition and voice conversion</p>
          </div>
          <div className="user-section">
            <span className="user-email">Welcome, {user.email}</span>
            <button className="signout-btn" onClick={handleSignOut}>
              Sign Out
            </button>
          </div>
        </div>
      </header>

      <main className="app-main">
        <div className="webcam-section">
          <div className="webcam-container">
            {webcamError ? (
              <div className="webcam-error">
                <p>{webcamError}</p>
                <button onClick={() => window.location.reload()}>
                  Reload Page
                </button>
              </div>
            ) : (
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                width={400}
                height={300}
                onUserMediaError={handleWebcamError}
                className="webcam-feed"
              />
            )}

            {isRecording && (
              <div className="recording-indicator">
                <div className="recording-dot"></div>
                Recording...
              </div>
            )}
          </div>

          <div className="controls">
            <button
              className={`control-btn ${isRecording ? 'stop' : 'start'}`}
              onClick={isRecording ? stopRecording : startRecording}
              disabled={webcamError}
            >
              {isRecording ? '‚èπÔ∏è Stop Recording' : '‚ñ∂Ô∏è Start Recording'}
            </button>

            <button
              className="control-btn capture"
              onClick={capture}
              disabled={isLoading || webcamError}
            >
              {isLoading ? 'üîÑ Processing...' : 'üì∏ Capture & Translate'}
            </button>
          </div>
        </div>

        <div className="results-section">
          {prediction && (
            <div className="prediction-result">
              <h2>Translation Result</h2>
              <div className="prediction-content">
                <span className="predicted-text">{prediction}</span>
                <div className="confidence-bar">
                  <div
                    className="confidence-fill"
                    style={{ width: `${confidence}%` }}
                  ></div>
                  <span className="confidence-text">{confidence}% confidence</span>
                </div>
                <p className="prediction-description">
                  {signLanguageData[prediction]}
                </p>
                <button
                  className="speak-btn"
                  onClick={() => speakText(prediction)}
                >
                   Speak
                </button>
              </div>
            </div>
          )}

          {translationHistory.length > 0 && (
            <div className="history-section">
              <div className="history-header">
                <h3>Recent Translations</h3>
                <button className="clear-btn" onClick={clearHistory}>
                  Clear History
                </button>
              </div>
              <div className="history-list">
                {translationHistory.map((entry) => (
                  <div key={entry.id} className="history-item">
                    <div className="history-content">
                      <span className="history-text">{entry.prediction}</span>
                      <span className="history-time">{entry.timestamp}</span>
                    </div>
                    <div className="history-actions">
                      <span className="history-confidence">{entry.confidence}%</span>
                      <button
                        className="history-speak-btn"
                        onClick={() => speakText(entry.prediction)}
                      >

                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </main>

      <footer className="app-footer">
        <p>Built with React ‚Ä¢ Sign Language Recognition ‚Ä¢ Voice Synthesis</p>
      </footer>
    </div>
  );
};

export default App;
